(() => {
  const selectors = [
    ["https://explorecourses.stanford.edu/search.*", "#search_courses_input"],
    ["https://carta-beta.stanford.edu.*", "#downshift-0-input"],
    ["https://www.google.com/search.*", "input[aria-label=\"Search\"]"],
  ];
  for ([url_regex, selector] of selectors) {
    if (window.location.href.match(new RegExp(url_regex))) {
      const el = document.querySelector(selector);
      if (el) {
        el.focus();
        el.selectionStart = 0;
        el.selectionEnd = el.value.length;
        break;
      }
    }
  }

})()
